mapper.update:
UPDATE PLT_BTN
SET SCRN_BTN_ID = #{SCRN_BTN_ID}
  , SCRN_BTN_NM = #{SCRN_BTN_NM}
  , BTN_TYPE    = #{BTN_TYPE}
  , MDFR_ID     = #{USER_ID}::INTEGER
  , MDFCN_DT = TO_CHAR(NOW(),'YYYYMMDDHH24MISS')
  , LAYOUT_ID = 
                    NULL
                    #{LAYOUT_ID}::INTEGER

WHERE BTN_ID = #{BTN_ID}:: INTEGER
