mapper.update:
   UPDATE PLT_PHN_QA_EVAL_SHT 
	  SET CHNG_ID = #{REG_ID}
	, CHNG_DTIM = TO_CHAR(SYSTIMESTAMP,'YYYYMMDDHH24MISS')
		 , QA_TY_L_CD = REPLACE(#{QA_TY_L_CD}, ',' , '/') 
		 , QA_TY_L = REPLACE(#{QA_TY_L}, ',' , '/') 
		 , QA_TY_M_CD = REPLACE(#{QA_TY_M_CD}, ',' , '/') 
		 , QA_TY_M = REPLACE(#{QA_TY_M}, ',' , '/') 
		 , QA_TY_S = #{QA_TY_S} 
		 , QA_TY_S_P = #{QA_TY_S_P} 
	WHERE QA_ID = #{QA_ID}

mapper.select:
SELECT QA_ID AS QA_ID
  FROM PLT_PHN_QA_EVAL_SHT
 WHERE QA_YM = #{QA_YM}
   AND QA_TY_ID = #{QA_TY_ID}
